import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { getApod } from '../services/apiService';
import './DataVisualization.css';
import {
  Chart as ChartJS,
  LineElement,
  PointElement,
  LinearScale,
  Title,
  CategoryScale,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  LineElement,
  PointElement,
  LinearScale,
  Title,
  CategoryScale,
  Tooltip,
  Legend
);

const DataVisualization = ({ data }) => {
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        // Fetch APOD data for the last 7 days
        const fetchChartData = async () => {
            const today = new Date();
            const promises = [];
            for (let i = 6; i >= 0; i--) {
                const date = new Date(today);
                date.setDate(today.getDate() - i);
                const dateStr = date.toISOString().split('T')[0];
                promises.push(getApod(dateStr));
            }
            const results = await Promise.all(promises);
            setChartData({
                labels: results.map(d => d.date),
                datasets: [
                    {
                        label: 'Explanation Length',
                        data: results.map(d => d.explanation.length),
                        fill: false,
                        borderColor: '#4fd1c5',
                        tension: 0.1
                    }
                ]
            });
        };
        fetchChartData();
    }, []);

    if (!data) return <div className="dv-no-data">No data available.</div>;

    return (
        <div className="dv-bg">
            <div className="dv-card">
                <h2 className="dv-title">{data.title}</h2>
                {data.media_type === 'image' && (
  <img
    className="dv-fullscreen-image"
    src={data.url}
    alt={data.title}
  />
)}
                {data.media_type === 'video' ? (
                    <div className="dv-video-wrapper">
                        <iframe
                            src={data.url}
                            title={data.title}
                            frameBorder="0"
                            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                            allowFullScreen
                        ></iframe>
                    </div>
                ) : null}
                <p className="dv-explanation">{data.explanation}</p>
                <div className="dv-date">{data.date}</div>
                {chartData && (
                    <div style={{ marginTop: '2rem' }}>
                        <h3>APOD Explanation Length (Last 7 Days)</h3>
                        <Line data={chartData} />
                    </div>
                )}
            </div>
        </div>
    );
};

export default DataVisualization;
