import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import DataVisualization from './components/DataVisualization';
import { getApod } from './services/apiService';
import './App.css'; 


const App = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [theme, setTheme] = useState('dark');
  const [selectedDate, setSelectedDate] = useState('');

  useEffect(() => {
    document.body.setAttribute('data-theme', theme);
  }, [theme]);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const apodData = await getApod();
        setData(apodData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleDateChange = async (e) => {
    const monthValue = e.target.value; // format: "YYYY-MM"
    setSelectedDate(monthValue);

    // If you want to fetch APOD for the first day of the selected month:
    const date = `${monthValue}-01`;
    setLoading(true);
    setError(null);
    try {
      const apodData = await getApod(date);
      setData(apodData);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRandomApod = async () => {
    setLoading(true);
    setError(null);
    try {
      const apodData = await getApod();
      setData(apodData);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return (
    <div className="spinner-container">
      <div className="spinner"></div>
    </div>
  );
  if (error) return <div>Error: {error}</div>;

  return (
    <Router>
      <div className="app-bg">
        <header className="app-header">
          <h1>
            <span role="img" aria-label="rocket">ðŸš€</span> NASA <span style={{ color: "#4fd1c5" }}>Data Visualization</span>
          </h1>
          <div className="controls">
            <input
              type="month"
              value={selectedDate}
              onChange={handleDateChange}
              className="date-picker"
            />
            <button className="theme-btn" onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}>
              {theme === 'dark' ? 'ðŸŒž Light' : 'ðŸŒ™ Dark'}
            </button>
            <button className="random-btn" onClick={handleRandomApod}>
              ðŸŽ² Random APOD
            </button>
          </div>
        </header>
        <main>
          <DataVisualization data={data} theme={theme} />
        </main>
      </div>
    </Router>
  );
};

export default App;
