import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { getApod } from '../services/apiService';
import './DataVisualization.css';
import {
  Chart as ChartJS,
  LineElement,
  PointElement,
  LinearScale,
  Title,
  CategoryScale,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  LineElement,
  PointElement,
  LinearScale,
  Title,
  CategoryScale,
  Tooltip,
  Legend
);

const DataVisualization = ({ data, theme }) => {
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        // Fetch APOD data for the last 7 days
        const fetchChartData = async () => {
            const today = new Date();
            const promises = [];
            for (let i = 6; i >= 0; i--) {
                const date = new Date(today);
                date.setDate(today.getDate() - i);
                const dateStr = date.toISOString().split('T')[0];
                promises.push(getApod(dateStr));
            }
            const results = await Promise.all(promises);
            const lineColor = theme === 'light' ? '#0077b6' : '#4fd1c5';
            setChartData({
                labels: results.map(d => d.date),
                datasets: [
                    {
                        label: 'Explanation Length',
                        data: results.map(d => d.explanation.length),
                        fill: false,
                        borderColor: lineColor,
                        backgroundColor: lineColor,
                        pointBorderColor: lineColor,
                        pointBackgroundColor: lineColor,
                        tension: 0.1
                    }
                ]
            });
        };
        fetchChartData();
    }, [theme]);

    if (!data) return <div className="dv-no-data">No data available.</div>;

    // Choose color based on theme
    const chartTextColor = theme === 'light' ? '#222' : '#f8fafc';
    const chartGridColor = theme === 'light' ? 'rgba(0,0,0,0.07)' : 'rgba(200,200,200,0.1)';
    const lineColor = theme === 'light' ? '#0077b6' : '#4fd1c5';

    return (
        <div className="dv-bg">
            <div className="dv-card">
                <h2 className="dv-title">{data.title}</h2>
                {data.media_type === 'image' ? (
                    <img className="dv-image" src={data.url} alt={data.title} />
                ) : data.media_type === 'video' ? (
                    <div className="dv-video-wrapper">
                        <iframe
                            src={data.url}
                            title={data.title}
                            frameBorder="0"
                            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                            allowFullScreen
                        ></iframe>
                    </div>
                ) : null}
                <p className="dv-explanation">{data.explanation}</p>
                <div className="dv-date">{data.date}</div>
                {chartData && (
                    <div className="dv-chart-container">
                        <Line
  data={chartData}
  options={{
    plugins: {
      legend: {
        labels: { color: chartTextColor }
      },
      title: {
        display: true,
        text: 'APOD Explanation Length (Last 7 Days)',
        color: chartTextColor,
        font: { size: 18 }
      }
    },
    scales: {
      x: {
        ticks: { color: chartTextColor },
        grid: { color: chartGridColor }
      },
      y: {
        ticks: { color: chartTextColor },
        grid: { color: chartGridColor }
      }
    }
  }}
                        />
                    </div>
                )}
            </div>
        </div>
    );
};

export default DataVisualization;
